import React, {createContext, useState} from 'react';

import dataJson from '../data.json';

export const CartContext = createContext();

const CartProvider = ({children}) => {
    const [cart, addToCart] = useState([]);
    const [count, setCount] = useState(1);

    const sumItems = cartItems => {
        let itemCount = cartItems.reduce((total, product) => total + product.quantity, 0);

        return  itemCount
    }

    const selectProduct = (prodID) => {
        const product = dataJson.filter(p => p.id === prodID)[0];
        product.quantity = 1;
        if(!cart.find(item => item.id === product.id)){
            addToCart([
                ...cart,
                product //tambien puede ser ...product y borrar el [0]
            ]);
        } else {
            product.quantity += count
        }


    }
    const addMore = (prodQty) =>{
        setCount(count + 1)
        prodQ
        if(count === 10){
            alert('No mas de 10')
            setCount(10)
        }
    }
    const lessMore = () =>{
        setCount(count - 1)
        if(count === 1){
            alert('No menos de 1')
            setCount(1)
        }
    }

    const deleteProduct = (prodId) => {
        const product = cart.findIndex(p => p.id === prodId)
        addToCart(product);
    }

    const cleanCart = () => addToCart([]);

    return ( 
        <CartContext.Provider
            value={{
                cart,
                qty: cart.length,
                count,
                selectProduct,
                addMore,
                lessMore,
                deleteProduct,
                cleanCart
            }}
        >
            {children}
        </CartContext.Provider>
     );
}
 
export default CartProvider;
