import React, { useContext } from 'react';
import Quantity from './Quantity';
import {CartContext} from '../context/CartContext';
import {ProductContext} from '../context/ProductContext';
import {Link, useParams} from 'react-router-dom';

const Details = () => {

    const cartContext = useContext(CartContext);
    const productContext = useContext(ProductContext);

    const { cart, selectProduct, addMore, more, setMore } = cartContext;
    const { dataJson } = productContext;

    const { id } = useParams();

    const details = dataJson.filter(item => item.id === id);

   return (
       <React.Fragment>
            {details.map(d => {
                const images = require(`../img/${d.img}`);
                return (
                    <div className="container" key={d.id}>
                        <div className="row">
                            <div className="img-container">
                                <img src={images} alt=""/>
                            </div>
                            <div className="details-container">
                                <div className="line"></div>
                                <h4 className="product-price">{d.price}</h4>
                                <h5 className="card-title">{d.name}</h5>
                                <p className="description">{d.description}</p>
                                {more && (
                                    <div>
                                        <Quantity
                                            prod={d}
                                        />
                                        <div>You can add more if you like :D</div>
                                    </div>

                                )}
                                {!more && (
                                    <button className="checkout-btn" 
                                        type="button" 
                                        onClick={cart.find(item => item.id === d.id) ? addMore(d) : selectProduct()}
                                    >ADD TO CART<span>{(d.quantity === undefined) ? 0 : d.quantity}</span></button>
                                )}
                            </div>
                            <Link to={'/'} className='goBack'><i className="fas fa-arrow-left"></i></Link>
                        </div>
                    </div>
                )
            })}
       </React.Fragment>


   )
}

export default Details;
